import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    shownElements: {
        "ponasanje": false,
        "saglasnost": false,
        "nekadaKoristiliTinder": true,
        "orijentacija": false,
        "osobine": false,
        "godiste": false,
        "pol": false,
        "instrukcije2": false,
    },
    questions: {
        ponasanje: "",
        saglasnost: "",
        nekadaKoristiliTinder: "",
        izasliTinder: "",
        orijentacija: "",
        instrukcije: "",
        osobine: "",
        godiste: "",
        pol: "",
        instrukcije2: "",
        osobine: "",
    }
}

export const hideReducer = createSlice({
    name: "hide",
    initialState,
    reducers: {
        toggleVisibility: (state, action) => {
            state.shownElements[action.payload.current] = false
            state.shownElements[action.payload.next] = true
        }
    }
})

export const { toggleVisibility } = hideReducer.actions

export default hideReducer.reducer
