import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    shownElements: {
        "ponasanje": true,
        "saglasnost": false,
        "nekadaKoristiliTinder": false,
        "orijentacija": false,
        "osobine": false,
        "godiste": false,
        "pol": false,
        "instrukcije2": false,
    }
}

export const hideReducer = createSlice({
    name: "hide",
    initialState,
    reducers: {
        toggleVisibility: (state, action) => {
            state.shownElements[action.payload.current] = false
            console.log(action);
            // state = state
        }
    }
})

export const { toggleVisibility } = hideReducer.actions

export default hideReducer.reducer
