function SwipeLeftOrRight(props) {
  const showElement = useSelector(state => state.hide.shownElements);
  const questionElement = useSelector(state => state.hide.questions);
  const dbToChose = useSelector(state => state.hide.dbPics);
  const show = true;
  const db = questionElement.pol === "Muški" ? dbToChose.dbMuski : dbToChose.dbZenska
  const characters = db

  const context = props.content
  const key = props.keyForContext
  const dispatch = useDispatch();
  let swiperResults = {}


  const swiped = (direction, url) => {
    let code = url.replace("/images/muska_lica/", "").replace("/images/zenska_lica/", "").replace(".jpg", "")
    swiperResults[code] = "dislike"
    if(direction == 'right'){
      swiperResults[code] = "like"
    }
    console.log(swiperResults);
    // if (Object.keys(swiperResults).length == db.length){
    //   dispatch(setAnswer({keyForQuestion: key, value: swiperResults}))
    // }
  }

  function setSwipe (code, direction) {
    swiperResults[code] = direction;
    if (Object.keys(swiperResults).length == db.length){
      dispatch(setAnswer({keyForQuestion: key, value: swiperResults}))
    }
  }

  function customTinderCard  (character) {
    let code = character.url.replace("/images/muska_lica/", "").replace("/images/zenska_lica/", "").replace(".jpg", "")

    return (

<Card className="text-center">
                      <Card.Img variant="top" src={character.url} style={{ width: "250px", alignSelf: "center" }} />
                      <Card.Body>
          <div className='buttons'>
            <Button variant="danger" className='swipeButton' onClick={() => setSwipe(code, 'left')}><img src="/images/cards/cross.png"/></Button>
            <Button variant="success" className='swipeButton' onClick={() => setSwipe(code, 'right')}><img src="/images/cards/tick.png"/></Button>
          </div>
</Card.Body>
</Card>   
    )
  }

  function sendAnswers () {
    dispatch(toggleVisibility({state: showElement, next: props.content.next, current: key}))
  }

  function shuffle(array) {
    let currentIndex = array.length,  randomIndex;

    // While there remain elements to shuffle.
    while (currentIndex != 0) {

      // Pick a remaining element.
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;

      // And swap it with the current element.
      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }

    return array;
  }

  // shuffle(characters);

  const Tinder = () => {
    return (
      <>
      <div className='cardContainer'>
          {characters.map((character) =>
                         customTinderCard (character)
          )}
        </div>
      </>
    )
  }

  return (
    <div>
      {showElement[key] ? 
        <Tinder/> : null
      }
    </div>
  )
}

export default SwipeLeftOrRight;
