import {useState} from 'react';
import {Modal, Button} from "react-bootstrap";

function RadioModal(content='') {
    function multiRadio (content) {
        const rows = [];
        content["options"].forEach(option => {
            rows.push(
                <Button variant="primary" onClick={handleClose}>{option.text}</Button>    
            )
        });
        return rows;
    }
  const [show, setShow] = useState(true);

  const handleClose = () => setShow(false);
  return (
    <>
      <Modal
      size="xl"
      show={show}
      >
        <Modal.Header closeButton>
            <Modal.Title>{content.content.headerText}</Modal.Title>
        </Modal.Header>

        <Modal.Footer>
            {multiRadio(content.content)}
        </Modal.Footer>
        </Modal>
    </>
  );
}

export default RadioModal;
