            <Button variant="outline-light" className='swipeButton' onClick={() => console.log('right')}><img src="/images/cards/tick.png"/></Button>
          </div>
</Card.Body>
</Card>      
            // <TinderCard className='swipe' key={character.url} onSwipe={(dir) => swiped(dir,character.url)} >
