import {useState} from 'react';
import {Modal, Button, Form} from "react-bootstrap";
import { useDispatch, useSelector } from 'react-redux';
import { toggleVisibility } from "../hideSlice";

function MultiRadioModal(content='') {
  const [show, setShow] = useState(true);

  function rowWithRadio (head, numOfRadios) {

    const rows = []
    const groupNum = Math.floor(Math.random() * 1000000);
    console.log(numOfRadios);
    for (let index = 1; index < numOfRadios + 1 ; index++) {
      const group = head;
      const id = "inline-"+groupNum+"-"+index
      rows.push(
        <Form.Check
          inline
          label={index}
          name={group}
          type='radio'
          id={id}
          key={id}
        />
      )
    }
    return <>{head} {rows}<br/></>
  }

  const handleClose = () => setShow(false);
  function loop(content) {
    const rows = []
    {content["multiRadioOptions"]["traits"].map((trait) => (
      rows.push(rowWithRadio(trait, content["multiRadioOptions"]["levels"]) )
      // <div key={`inline-radio`} className="mb-3">
      //   <Form.Check
      //     inline
      //     label="1"
      //     name="group1"
      //     type='radio'
      //     id={`inline-radio-1`}
      //   />
      // </div>
    ))}
    return rows
  }
  return (
    <>
      <Modal
      size="xl"
      show={show}
      >
        <Modal.Header>
            <Modal.Title>{content.content.headerText}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Form>
            {loop(content.content)}
          </Form>
        </Modal.Body>
        </Modal>
    </>
  );
}

export default MultiRadioModal;
