import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    shownElements: {
        ponasanje: false,
        saglasnost: false,
        nekadaKoristiliTinder: false,
        orijentacija: false,
        osobine: false,
        godiste: false,
        pol: false,
        instrukcije2: false,
    },
    questions: {
        ponasanje: "",
        saglasnost: "",
        nekadaKoristiliTinder: "",
        izasliTinder: "",
        orijentacija: "",
        instrukcije: "",
        osobine: "",
        godiste: "",
        pol: "",
        instrukcije2: "",
        tinderPictures: "",
    }
}

export const hideReducer = createSlice({
    name: "hide",
    initialState,
    reducers: {
        toggleVisibility: (state, action) => {
            state.shownElements[action.payload.current] = false
            state.shownElements[action.payload.next] = true
        },
        setAnswer: (state, action) => {
            console.log(action);
            // state.questions[action.payload.current] = false
            // state.questions[action.payload.next] = true
        }
    }
})

export const { toggleVisibility, setAnswer } = hideReducer.actions

export default hideReducer.reducer
