import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    shownElements: {
        ponasanje: false,
        saglasnost: false,
        nekadaKoristiliTinder: false,
        orijentacija: false,
        osobine: false,
        godiste: false,
        pol: false,
        instrukcije2: false,
        tinderPictures: true,
        perTinderPictures: false,
        endOfTest: false,
    },
    questions: {
        ponasanje: "",
        saglasnost: "",
        nekadaKoristiliTinder: "",
        izasliTinder: "",
        orijentacija: "",
        instrukcije: "",
        osobine: "",
        godiste: "",
        pol: "",
        instrukcije2: "",
        tinderPictures: "",
        perTinderPictures: "",
    }
}

export const hideReducer = createSlice({
    name: "hide",
    initialState,
    reducers: {
        toggleVisibility: (state, action) => {
            state.shownElements[action.payload.current] = false
            state.shownElements[action.payload.next] = true
        },
        setAnswer: (state, action) => {
            state.questions[action.payload.keyForQuestion] = action.payload.value
        },
        exitTest: (state, action) => {
            state.shownElements[action.payload.current] = false
            state.shownElements.endOfTest = true
        },
    }
})

export const { toggleVisibility, setAnswer, exitTest } = hideReducer.actions

export default hideReducer.reducer
