import { useState } from 'react';
import { Modal, Button, Form, Table } from "react-bootstrap";
import { useDispatch, useSelector } from 'react-redux';
import { toggleVisibility, setAnswer } from "../hideSlice";

function MultiRadioModal(props) {
  const context = props.content
  const key = props.keyForContext
  let radioChecker = {}
let osobineValues = {}

  const dispatch = useDispatch();
  const showElement = useSelector(state => state.hide.shownElements);
  const currentState = useSelector(state => state.hide.questions);

  function sendAnswers(key, radioChecker) {
    dispatch(setAnswer({ keyForQuestion: key, value: radioChecker, currentState }))
    dispatch(toggleVisibility({ state: showElement, next: props.content.next, current: key }))
  }

  function checkIfAllAreSelected(radioGroup, indexNum) {
    radioChecker[radioGroup] = indexNum
    let readyForDispatch = true;
    for (const radio in radioChecker) {
      if (radioChecker[radio] === 0) {
        readyForDispatch = false;
      }
    }
    if (readyForDispatch) {
      sendAnswers(key, radioChecker)
    }
  }

  function rowWithRadio(head, numOfRadios) {

    const rows = []
    const groupNum = Math.floor(Math.random() * 1000000);

    for (let index = 1; index < numOfRadios + 1; index++) {
      const group = head;
      const id = "inline-" + groupNum + "-" + index
      radioChecker[group] = 0;
      rows.push(
        <Form.Check
          inline
          label={index}
          name={group}
          type='radio'
          id={id}
          key={id}
          onClick={() => { checkIfAllAreSelected(group, index); }}
        />
      )
    }
    return <>{head} {rows}<br /></>
  }

  function checkSelected(osob, elem, key) {
    osobineValues[osob] = elem + 1
    if(Object.keys(osobineValues).length === 6){
        let tst = {}
        tst[key] = osobineValues
        sendAnswers(tst)
    }
  }

  function loop(content) {
    const rows = []
    {
      content["multiRadioOptions"]["traits"].map((trait) => (
        rows.push(rowWithRadio(trait, content["multiRadioOptions"]["levels"]))
      ))
    }
    return rows
  }
  return (
    <>
      <Modal
        size="xl"
        show={showElement[key]}
      >
        <Modal.Header>
          <Modal.Title>{context.headerText}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th></th>
                <th>1</th>
                <th>2</th>
                <th>3</th>
                <th>4</th>
                <th>5</th>
                <th>6</th>
                <th>7</th>
              </tr>
            </thead>
            <tbody>
              {context["multiRadioOptions"]["traits"].map((osob) => {
                return (
                  <tr>
                    <td>{osob}</td>
                    {[...Array(7)].map((elem, index) => (
                      <td><Form.Check inline name={osob} type='radio' id={elem} key={elem} onClick={() => { checkSelected(osob, index); }} /></td>
                    ))}
                  </tr>
                )
              })}
            </tbody>
          </Table>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default MultiRadioModal;
